mapper.update:
UPDATE orders
SET Money = #{order.Money},Addrid = #{order.Addrid},Pnum = #{order.Pnum},Total = #{order.Total}
WHERE Oid = #{oid};

UPDATE user
SET Ostate = #{order.Ostate}, Time = #{order.time}
WHERE Oid = #{oid};

mapper.select:
SELECT COUNT(*)
FROM orders
WHERE Uid = #{uid} AND Ostate = #{ostate};
