mapper.select:
SELECT * FROM user
WHERE Account = #{str}
OR Username = #{str};

mapper.update:
UPDATE user
SET Password = #{pwd}
WHERE Account = #{str}
OR Username = #{str};

UPDATE user
SET Username = #{usr.Username}, Tel = #{usr.Tel}, Email = #{usr.Email}
WHERE Account = #{acc};

UPDATE user
SET State = #{status}
WHERE Account = #{str}
   OR Username = #{str};

UPDATE user
SET IsBusiness = #{isbusiness}
WHERE Account = #{str}
   OR Username = #{str};
